import React from 'react';
import "./WeatherComponent.css";



export const WeatherInfoIcon = {
    sunset : "../images/night.svg",
    sunrise : "../images/day.svg",
    wind : "../images/wind.svg",
    pressure : "../images/pressure.svg",
    temp :  "../images/temp.svg",
    humidity : "../images/humidity.svg"
}


export const WeatherIcons = {
    "01d": "../images/sunny.svg",
    "01n": "../images/night.svg",
    "02d": "../images/day.svg",
    "02n": "../images/cloudy-night.svg",
    "03d": "../images/cloudy.svg",
    "03n": "../images/cloudy.svg",
    "04d": "../images/perfect-day.svg",
    "04n": "../images/cloudy-night.svg",
    "09d": "../images/rain.svg",
    "09n": "../images/rain-night.svg",
    "10d": "../images/rain.svg",
    "10n": "../images/rain-night.svg",
    "11d": "../images/storm.svg",
    "11n": "../images/storm.svg",
  };

const WeatherInfoComponent = (props) =>{
    const {name , value} = props;
    return(
        <>
        <div className='infocontainer'>
            <div className='infoicon'><img src={WeatherInfoIcon[name]} alt={name}></img> </div>
            <div className='infolable'>
                {value}
                <span>{name}</span>
            </div>
        </div>
        
        </>
    );
}
const WeatherComponent = (props) => {
    const {weather} = props;
    const isDay = weather?.weather[0].icon?.includes('d')
    const getTime = (timeStamp) => {
        return `${new Date(timeStamp * 1000).getHours()} : ${new Date(timeStamp * 1000).getMinutes()}`
    }
    return (
        <>
        <div className='app-weather'>
            <h1>Weather Details</h1>
        
        <div className='app-weather-temp'>
        <h1> <span>{`${Math.floor(weather?.main?.temp- 273)} Â°C`} </span> | </h1> <p> {`${weather?.weather[0].description}`}</p> 
        <img src={WeatherIcons[weather?.weather[0].icon]}/> 
        </div>
        
        <div className='app-weather-location'>
            <h1>{`${weather?.name} , ${weather?.sys?.country}`}</h1>
        </div>
        <div>
            <WeatherInfoComponent name= {isDay ? "sunset" : "sunrise"}   
              value={`${getTime(weather?.sys[isDay ? "sunset" : "sunrise"])}`}/>
            <WeatherInfoComponent name= "humidity"   value = {weather?.main?.humidity}/>
            <WeatherInfoComponent name= "wind"   value = {weather?.wind?.speed}/>
            <WeatherInfoComponent name= "pressure"   value = {weather?.main?.pressure}/>
        </div>
    </div>
        </>
    )
}

export default WeatherComponent
