import { useState } from 'react';
import axios from 'axios';
import './App.css';
import WeatherComponent from './components/WeatherComponent';
import CityComponent from './components/CityComponent';

export const WeatherIcons = {
  "01d": "../images/sunny.svg",
  "01n": "../images/night.svg",
  "02d": "../images/day.svg",
  "02n": "../images/cloudy-night.svg",
  "03d": "../images/cloudy.svg",
  "03n": "../images/cloudy.svg",
  "04d": "../images/perfect-day.svg",
  "04n": "../images/cloudy-night.svg",
  "09d": "../images/rain.svg",
  "09n": "../images/rain-night.svg",
  "10d": "../images/rain.svg",
  "10n": "../images/rain-night.svg",
  "11d": "../images/storm.svg",
  "11n": "../images/storm.svg",
};



function App() {
  const [city, setCity] = useState();
  const [weather, setWeather] = useState();

  const fatchWeather = async (e) =>{
    e.preventDefault();
    const response = await axios.get(
      `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=38ca4b8e07c7267756afb7f1e5fc9ac0`,
    );
    setWeather(response.data);
  }
  return (
    <div>

      { weather ?  <WeatherComponent  weather = {weather}/> : <CityComponent  setCity = {setCity} fatchWeather = {fatchWeather}/>}
      </div>
  );
  }

export default App;
